import './Style.css';
// import Button from 'material-ui/Button';
import { Button, FormGroup, FormControl,FormText, ControlLabel } from "react-bootstrap";
import React, { useState, useEffect,Component } from 'react'  
import axios from 'axios';  
import { Card, CardBody, CardFooter, Col, Container, Form, Input, InputGroup, InputGroupAddon, InputGroupText, Row } from 'reactstrap'; 

class AddBook extends Component {
    state={
        books:[]
    }
    componentWillMount() {
        axios.get('localhost:8080/getBook').then((response) =>{
            this.useState({
                books:response.data
            })
        })
    }
    render(){
        let books= this.state.books.map((book) => {
            return <tr key={book.id}>
                <td >{book.id}</td>
                <td >{book.BookName}</td>
                <td >{book.Edition}</td>
                <td>{book.Author}</td>
                <td >{book.Date}</td>
                <td >{book.Price}</td>
                <td><Button color="success" size="sm" >Edit</Button></td>
                <td><Button color="danger" size="sm">Delete</Button></td>
            </tr>
        });
        return(
        <div class="bs-example">
            <table className="table table-striped table-dark" >
                <thead>
                    <tr>
                        <th >Bookid</th>
                        <th >BookName</th> 
                        <th>Edition</th> 
                        <th >Author</th>
                        <th >Date</th>
                        <th >Amount</th>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                        {book}
                    </tr>
                </tbody>
            </table>
        </div>
        )
    }
}
export default AddBook;













































































