import React, { Component } from 'react';
import './style.css';
class AddBook extends Component {
    state={
        ...this.returnStateobject()
    }
    returnStateobject() {
        if(this.props.currentIndex== -1)
            return{
                BookName:'',
                Edition:'',
                Subject:'',
                Date:'',
                Author:'',
                Price:''
            }
        else
            return this.props.list[this.props.currentIndex]
    }
    componentDidUpdate(prevProps) {
        if (prevProps.currentIndex != this.props.currentIndex || prevProps.list != this.props.list) {
            this.setState({ ...this.returnStateobject() })
            console.log(prevProps, this.props)
        }
    }
    handleInputChange = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        })
    }
    handleSubmit = (e) => {
        e.preventDefault()
        this.props.onAddOrEdit(this.state)
    }
    render() {
        return (
            <form onSubmit={this.handleSubmit} autoComplete="off" className={display}>
               BookName: <input name="BookName" type="text" placeholder="BookNamer" onChange={this.handleInputChange} value={this.state.BookName} /><br />
               Edition : < input name="Edition" type="number" placeholder="Edition" onChange={this.handleInputChange} value={this.state.Edition} /><br />
               Subject : < input name="Subject" type="text" placeholder="Subject" onChange={this.handleInputChange} value={this.state.Subject} /><br />
               Author  : < input name="Author" type="text" placeholder="Author" onChange={this.handleInputChange} value={this.state.Author} /><br />
               Price   : < input name="Price" type="number" placeholder="Price" onChange={this.handleInputChange} value={this.state.Price} /><br />
               Date    : < input name="Date" type="Date" placeholder="Date" onChange={this.handleInputChange} value={this.state.Date} /><br />
                <button type="submit">Save</button>
            </form>
        )
    }

}
export default AddBook;
