import React, { Component } from 'react';
import AddBook from './AddBook'
import { Button } from 'react-bootstrap';

class ListBook extends Component {
    constructor(props){
        super();
        this.state={
            search:''
        }
    }
    state = {
        currentIndex: -1,
        list: this.returnList()
    }
    returnList() {
        if (localStorage.getItem('Books') == null)
            localStorage.setItem('Books', JSON.stringify([]))
        return JSON.parse(localStorage.getItem('Books'))
    }
    handleEdit = index => {
        this.setState({
            currentIndex: index
        })
    }
    handleDelete = (index) => {
        var list = this.returnList()
        list.splice(index, 1);
        localStorage.setItem('Books', JSON.stringify(list))
        this.setState({ list, currentIndex: -1 })
    }
    onAddOrEdit = (data) => {
        var list = this.returnList()
        if (this.state.currentIndex == -1)
            list.push(data)
        else
            list[this.state.currentIndex] = data
        localStorage.setItem('Books', JSON.stringify(list))
        this.setState({ list, currentIndex: -1 })
    }
    updateSearch(event) {
        this.setState({search:event.target.value.substr(0,20)});
    }
    render() {
        let filteredid=this.props.data.filter((data)=>{return data.id.toLowerCase().indexOf(this.state.search.toLowerCase())!==-1;
        }
        );
        return (
            <div>
                <AddBook
                    currentIndex={this.state.currentIndex}
                    list={this.state.list}
                    onAddOrEdit={this.onAddOrEdit}
                />
                <hr />
                <table>
                    <tbody>
                        <td>BookName</td> <td>Edition</td> <td>Subject</td><td>Author</td><td>Date</td><td>Price</td>
                        {this.state.list.map((item, index) => {
                            return <tr key={index}>
                                <td>{item.BookName}</td>
                                <td>{item.Edition}</td>
                                <td>{item.Subject}</td>
                                <td>{item.Author}</td>
                                <td>{item.Date}</td>
                                <td>{item.Price}</td>
                                <td><button onClick={() => this.handleEdit(index)}>Edit</button></td>
                                <td><button onClick={() => this.handleDelete(index)}>Delete</button></td>
                            </tr>
                        })}
                    </tbody>
                </table>
            </div>
        )
        return(
            <div>
            <form>
                    <input type="text"  placeholder="Search for..." value={this.state.search} onChange={this.updateSearch.bind(this)}/>
                </form>
                  <ul>  
                        {filteredid.map((data)=>{
                            return<Data data={data} key={data.id}/>
                        })}</ul>
                        </div>
        );
    }



}
export default ListBook;
