import React, { Component } from 'react';
import AddBook from './AddBook';
import './Style.css';
import { makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';

const useStyles = makeStyles({
    table: {
      minWidth: 650,
    },
  });
const columns = [
    { id: 'Bookname', label: 'BookName', minWidth: 170 },
    { id: 'Edition', label: 'Edition', minWidth: 100 },
    {
      id: 'Author',
      label: 'Author',
      minWidth: 170,
      align: 'right',
      format: (value) => value.toLocaleString(),
    },
    {
      id: 'Price',
      label: 'Price(Rs)',
      minWidth: 170,
      align: 'right',
      format: (value) => value.toLocaleString(),
    },
    {
      id: 'Date',
      label: 'Date',
      minWidth: 170,
      align: 'right',
      format: (value) => value.toFixed(2),
    },
  ];
function ListBook() {
    classes = useStyles();
    state = {
        currentIndex: -1,
        list: this.returnList()
    }
   function returnList() {
        if (localStorage.getItem('Books') == null)
            localStorage.setItem('Books', JSON.stringify([]))
        return JSON.parse(localStorage.getItem('Books'))
    }
    handleEdit = index => {
        this.setState({
            currentIndex: index
        })
    }
    handleDelete = (index) => {
        var list = this.returnList()
        list.splice(index, 1);
        localStorage.setItem('Books', JSON.stringify(list))
        this.setState({ list, currentIndex: -1 })
    }
    onAddOrEdit = (data) => {
        var list = this.returnList()
        if (this.state.currentIndex == -1)
            list.push(data)
        else
            list[this.state.currentIndex] = data
        localStorage.setItem('Books', JSON.stringify(list))
        this.setState({ list, currentIndex: -1 })
    }
        return (
            <div>
                <AddBook
                    currentIndex={this.state.currentIndex}
                    list={this.state.list}
                    onAddOrEdit={this.onAddOrEdit}
                />
                <hr />
                <h3>List of books</h3>
                <TableContainer component={Paper}>
      <Table className={classes.table} size="small" aria-label="a dense table">
        <TableHead>
          <TableRow>
            <TableCell>Bookid </TableCell>
            <TableCell align="right">BookName</TableCell>
            <TableCell align="right">Edition</TableCell>
            <TableCell align="right">Author</TableCell>
            <TableCell align="right">Price</TableCell>
            <TableCell align="right">Date</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
              {this.state.list.map((item, index) => {
                            return <tr key={index}>
              <TableCell align="right">{index}</TableCell>
              <TableCell align="right">{index.BookName}</TableCell>
              <TableCell align="right">{index.Edition}</TableCell>
              <TableCell align="right">{index.Author}</TableCell>
              <TableCell align="right">{index.Price}</TableCell>
              <TableCell align="right">{index.Date}</TableCell>
              </tr>
            })}
        </TableBody>
      </Table>
    </TableContainer>
