import React, { Component } from 'react';
import './Style.css';
import TextField from "material-ui/TextField";
import Button from 'material-ui/Button';
class AddBook extends Component {
    state={
        ...this.returnStateobject()
    }
    returnStateobject() {
        if(this.props.currentIndex== -1)
            return{
                BookName:'',
                Edition:'',
                Author:'',
                Date:'',
                Price:''
            }
        else
            return this.props.list[this.props.currentIndex]
    }
    componentDidUpdate(prevProps) {
        if (prevProps.currentIndex != this.props.currentIndex || prevProps.list != this.props.list) {
            this.setState({ ...this.returnStateobject() })
            console.log(prevProps, this.props)
        }
    }
    handleInputChange = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        })
    }
    handleSubmit = (e) => {
        e.preventDefault()
        this.props.onAddOrEdit(this.state)
    }
    render() {
        return (
            <form>
            <TextField
              name="BookName"
              type="text"
              hintText="Book name"
              floatingLabelText="Book name"
              value={this.state.BookName}
              placeholder="BookName"
              floatingLabelFixed
            />
            <br />
            <TextField
              name="Edition"
              hintText="Edition"
              type="number"
              floatingLabelText="Edition"
              placeholder="Edition"
              value={this.state.Edition}
              floatingLabelFixed
            />
            <br />
            <TextField
              name="Author"
              hintText="Author"
              type="text"
              floatingLabelText="Author"
              placeholder="Author"
              value={this.state.Author}
              floatingLabelFixed
            />
            <br />
            <TextField
              name="Price"
              hintText="Price"
              type="number"
              floatingLabelText="Price"
              placeholder="Price"
              value={this.state.Price}
              floatingLabelFixed
            />
            <br />
            <TextField
              name="Date"
              hintText="Date"
              type="Date"
              floatingLabelText="Date"
              placeholder="Date"
              value={this.state.Date}
              floatingLabelFixed
            />
            <br />
            {/* <div>
            <h2>AddBook</h2>
            <form onSubmit={this.handleSubmit} className="display">
                <label>BookName</label>
                <input name="BookName" type="text" placeholder="BookName" onChange={this.handleInputChange} value={this.state.BookName} /><br />
                <label>Edition</label>
                < input name="Edition" type="number" placeholder="Edition" onChange={this.handleInputChange} value={this.state.Edition} /><br />
                <label>Author</label>
                < input name="Author" type="text" placeholder="Author" onChange={this.handleInputChange} value={this.state.Author} /><br />
                <label>Price</label>
                < input name="Price" type="number" placeholder="Price" onChange={this.handleInputChange} value={this.state.Price} /><br />
                <label>Date</label>
                < input name="Date" type="Date" placeholder="Date" onChange={this.handleInputChange} value={this.state.Date} /><br />
                <button>Save</button>
            </form>
            </div> */}
             <Button label="Submit" color="secondary" >save</Button>
             </form>
        )
       
    }

}
export default AddBook;
