import './Style.css';
import { Table } from 'react-bootstrap';
import {Button}from 'react-bootstrap';
import React, { Component } from 'react'  
import axios from 'axios';  

class AddBook extends Component {
    constructor(props){
        super(props);
    this.state={
        error:null,
        books:[],
        response:{}
    }
}
    componentDidMount() {
        axios.get('http://localhost:8080/books').then((response) =>{
            this.setState({
                books: response.data
            })
        },
        (error)=>{  
            this.setState({error});
        }
        );
    }
    delete(Bookid) {  
        const { books } = this.state;     
       axios.delete('http://localhost:8080/books/${this.state.bookid}' , {Bookid}).then(result=>{  
         alert(result.data);  
          this.setState({  
            response:result,  
            books:books.filter(Book=>Book.bookid !== Bookid)  
          });  
        });  
      }

    render(){
        const{error,books}=this.state;  
        if(error){  
            return(  
                <div>Error:{error.message}</div>  
            )  
        }  
        else  
        {
        return(
        <div className="bs-example">
            <Table className="table table-striped table-dark">
                <thead className="btn-primary">
                    <tr>
                        <th >#</th>
                        <th >BookName</th> 
                        <th>Edition</th> 
                        <th >Author</th>
                        <th >Date</th>
                        <th >Amount</th>
                    </tr>
                </thead>
                <tbody>
                {books.map(Book => (
                <tr key={Book.bookid}>
                <td >{Book.bookid}</td>
                <td >{Book.bookname}</td>
                <td >{Book.edition}</td>
                <td>{Book.author}</td>
                <td >{Book.date}</td>
                <td >{Book.amount}</td>
                <td><Button size="la" classname="mr-2" variant="info" onClick={() => this.props.edit(Book.bookid)}>Edit</Button>       
                    <Button variant="danger" onClick={() => this.delete(Book.bookid)}>Delete</Button>
                </td>
            </tr>
        ))}
                </tbody>
            </Table>
            </div>
        );
    }
}
}
export default AddBook;













































































