import React from 'react';  
import axios from 'axios';  
import './Style.css'  
import { Container, Col, Form, Row, FormGroup, Label, Input, Button } from 'reactstrap';  
class AddBook extends Component{  
constructor(props){  
super(props)  
this.state = {  
    bookname:'',
    edition:'',
    author:'',
    date:'',
    amount:'',  
}  
}   
Addstudent=()=>{  
  axios.post('http://localhost:8080/books', {bookname:this.state.bookname,edition:this.state.edition,  
  author:this.state.author, data:this.state.date,amount:this.state.amount})  
.then(json => {  
if(json.data.Status==='Success'){  
  console.log(json.data.Status);  
  alert("Data Save Successfully");  
this.props.history.push('http://localhost:8080/books')  
}  
else{  
alert('Data not Saved');  
debugger;  
this.props.history.push('http://localhost:8080/books')  
}  
})  
}  
   
handleChange= (e)=> {  
this.setState({[e.target.name]:e.target.value});  
}  
   
render() {  
return (  
   <Container className="App">  
    <h4 className="PageHeading">Enter Book Informations</h4>  
    <Form className="form">  
      <Col>  
        <FormGroup row>  
          <Label for="bookname" sm={2}>BookName</Label>  
          <Col sm={10}>  
            <Input type="text" name="bookname" onChange={this.handleChange} value={this.state.bookname} placeholder="Enter Name" />  
          </Col>  
        </FormGroup>  
        <FormGroup row>  
          <Label for="author" sm={2}>Author</Label>  
          <Col sm={10}>  
            <Input type="text" name="author" onChange={this.handleChange} value={this.state.author} placeholder="Enter Author" />  
          </Col>  
        </FormGroup>  
        <FormGroup row>  
          <Label for="edition" sm={2}>Edition</Label>  
          <Col sm={10}>  
            <Input type="number" name="edition" onChange={this.handleChange} value={this.state.edition} placeholder="Enter edition" />  
          </Col>  
        </FormGroup>  
        <FormGroup row>  
          <Label for="date" sm={2}>Date</Label>  
          <Col sm={10}>  
            <Input type="date" name="date" onChange={this.handleChange} value={this.state.date} placeholder="Enter date" />  
          </Col>  
        </FormGroup>  
        <FormGroup row>  
          <Label for="amount" sm={2}>Amount</Label>  
          <Col sm={10}>  
            <Input type="number" name="amount" onChange={this.handleChange} value={this.state.amount} placeholder="Enter Amount" />  
          </Col>  
        </FormGroup>  
      </Col>  
      <Col>  
        <FormGroup row>  
          <Col sm={5}>  
          </Col>  
          <Col sm={1}>  
          <button type="button" onClick={this.Addstudent} className="btn btn-success">Submit</button>  
          </Col>  
          <Col sm={1}>  
            <Button color="danger">Cancel</Button>{' '}  
          </Col>  
          <Col sm={5}>  
          </Col>  
        </FormGroup>  
      </Col>  
    </Form>  
  </Container>  
);  
}  
   
}  
   
