import React, { Component } from 'react';
import AddBook from './AddBook';
import './Style.css';


const columns = [
    { id: 'Bookname', label: 'BookName', minWidth: 170 },
    { id: 'Edition', label: 'Edition', minWidth: 100 },
    {
      id: 'Author',
      label: 'Author',
      minWidth: 170,
      align: 'right',
      format: (value) => value.toLocaleString(),
    },
    {
      id: 'Price',
      label: 'Price(Rs)',
      minWidth: 170,
      align: 'right',
      format: (value) => value.toLocaleString(),
    },
    {
      id: 'Date',
      label: 'Date',
      minWidth: 170,
      align: 'right',
      format: (value) => value.toFixed(2),
    },
  ];
class ListBook extends Component {
    state = {
        currentIndex: -1,
        list: this.returnList()
    }
    returnList() {
        if (localStorage.getItem('Books') == null)
            localStorage.setItem('Books', JSON.stringify([]))
        return JSON.parse(localStorage.getItem('Books'))
    }
    handleEdit = index => {
        this.setState({
            currentIndex: index
        })
    }
    handleDelete = (index) => {
        var list = this.returnList()
        list.splice(index, 1);
        localStorage.setItem('Books', JSON.stringify(list))
        this.setState({ list, currentIndex: -1 })
    }
    onAddOrEdit = (data) => {
        var list = this.returnList()
        if (this.state.currentIndex == -1)
            list.push(data)
        else
            list[this.state.currentIndex] = data
        localStorage.setItem('Books', JSON.stringify(list))
        this.setState({ list, currentIndex: -1 })
    }
    render() {
        return (
            <div>
                <AddBook
                    currentIndex={this.state.currentIndex}
                    list={this.state.list}
                    onAddOrEdit={this.onAddOrEdit}
                />
                <hr />
                <h3>List of books</h3>
                <table className="Table" align="center" >
                    <thead>
                        <tr>
                            <th className="format">Bookid</th>
                            <th className="format">BookName</th> 
                            <th className="format">Edition</th> 
                            <th className="format">Author</th>
                            <th className="format">Date</th>
                            <th className="format">Price</th>
                        </tr>
                    </thead>
                    <tbody>
                        {this.state.list.map((item, index) => {
                            return <tr key={index}>
                                 <th className="formate">{index}</th>
                                <th className="formate">{item.BookName}</th>
                                <th className="formate">{item.Edition}</th>
                                <th className="formate">{item.Author}</th>
                                <th className="formate">{item.Date}</th>
                                <th className="formate">{item.Price}</th>
                                <th><button className="button" onClick={() => this.handleEdit(index)}>Edit</button></th>
                                <th><button className="button" onClick={() => this.handleDelete(index)}>Delete</button></th>
                            </tr>
                        })}
                    </tbody>
                </table>
            </div>
        );
    }

}
export default ListBook;
