import './Style.css';
import { Table } from 'react-bootstrap';
import {Button,Card, CardBody, CardFooter}from 'react-bootstrap';
import React, { Component } from 'react'  
import axios from 'axios';  

class AddBook extends Component {
    state={
        books:[]
    }
    componentWillMount() {
        axios.get('http://localhost:8080/books').then((response) =>{
            this.setState({
                books: response.data
            })
        });
    }
    render(){
        return(
            <div className="animated fadeIn">  
                <Row>
                    <Col>
                        <Card>  
                            <CardHeader>  
                                <i className="fa fa-align-justify"></i> Employee List  
                            </CardHeader>  
                            <CardBody>  
                                <Table hover bordered striped responsive size="sm">
                                 <thead> 
                         <tr>
                        <th >#</th>
                        <th >BookName</th> 
                        <th>Edition</th> 
                        <th >Author</th>
                        <th >Date</th>
                        <th >Amount</th>
                    </tr>
                </thead>
                <tbody>
                {this.state.books.map((book) => {
            return <tr key={book.id}>
                <td >{book.id}</td>
                <td >{book.bookname}</td>
                <td >{book.edition}</td>
                <td>{book.author}</td>
                <td >{book.date}</td>
                <td >{book.amount}</td>
                <td>
                    <Button color="success" size="sm" className="mr-2">Edit</Button>
                    <Button color="danger" size="sm">Delete</Button></td>
            </tr>
        })}
                </tbody>
            </Table>
            </CardBody>
            </Card>
            </Col>
            </Row>
            </div>
        );
    }
}
export default AddBook;













































































