import React from 'react';   
import { Container, Col, Form, Row, FormGroup, Label, Input, Button } from 'reactstrap';  
import axios from 'axios'  
import './Style.css'  
class Edit extends React.Component {  
    constructor(props) {  
        super(props)  
     
    this.onChangebookname = this.onChangebookname.bind(this);  
    this.onChangeauthor = this.onChangeauthor.bind(this);  
    this.onChangeedition = this.onChangeedition.bind(this);  
    this.onChangedate = this.onChangedate.bind(this); 
    this.onChangeamount = this.onChangeamount.bind(this);  
    this.onSubmit = this.onSubmit.bind(this);  
  
         this.state = {  
            bookname:'',
            edition:'',
            author:'',
            date:'',
            amount:'',    
  
        }  
    }  
  
  componentDidMount() {  
      axios.get('http://localhost:8080/books'+this.props.match.params.bookid)  
          .then(response => {  
              this.setState({   
                bookname: response.data.bookname,   
                edition: response.data.edition,  
                author: response.data.author,  
                date: response.data.date,
                amount: response.data.amount });  
  
          })  
          .catch(function (error) {  
              console.log(error);  
          })  
    }  
  
  onChangebookname(e) {  
    this.setState({  
        bookname: e.target.value  
    });  
  }  
  onChangeauthor(e) {  
    this.setState({  
        author: e.target.value  
    });    
  }  
  onChangeedition(e) {  
    this.setState({  
        edition: e.target.value  
    });  
}  
    onChangedate(e) {  
        this.setState({  
            date: e.target.value  
        });  
  }  
  onChangeamount(e) {  
    this.setState({  
        amount: e.target.value  
    });  
} 
  
  onSubmit(e) {  
    debugger;  
    e.preventDefault();  
    const obj = {  
        bookid:this.props.match.params.bookid,  
      bookname: this.state.bookid,  
      author: this.state.author,  
      edition: this.state.edition,  
      date: this.state.date,
      amount: this.state.amount 
  
    };  
    axios.post('http://localhost:8080/books', obj)  
        .then(res => console.log(res.data));  
        debugger;  
        this.props.history.push('http://localhost:8080/books')  
  }  
    render() {  
        return (  
            <Container className="App">  
  
             <h4 className="PageHeading">Update Book Informations</h4>  
                <Form className="form" onSubmit={this.onSubmit}>  
                <Col>  
        <FormGroup row>  
          <Label for="bookname" sm={2}>BookName</Label>  
          <Col sm={10}>  
            <Input type="text" name="bookname" onChange={this.handleChange} value={this.state.bookname} placeholder="Enter Name" />  
          </Col>  
        </FormGroup>  
        <FormGroup row>  
          <Label for="author" sm={2}>Author</Label>  
          <Col sm={10}>  
            <Input type="text" name="author" onChange={this.handleChange} value={this.state.author} placeholder="Enter Author" />  
          </Col>  
        </FormGroup>  
        <FormGroup row>  
          <Label for="edition" sm={2}>Edition</Label>  
          <Col sm={10}>  
            <Input type="number" name="edition" onChange={this.handleChange} value={this.state.edition} placeholder="Enter edition" />  
          </Col>  
        </FormGroup>  
        <FormGroup row>  
          <Label for="date" sm={2}>Date</Label>  
          <Col sm={10}>  
            <Input type="date" name="date" onChange={this.handleChange} value={this.state.date} placeholder="Enter date" />  
          </Col>  
        </FormGroup>  
        <FormGroup row>  
          <Label for="amount" sm={2}>Amount</Label>  
          <Col sm={10}>  
            <Input type="number" name="amount" onChange={this.handleChange} value={this.state.amount} placeholder="Enter Amount" />  
          </Col>  
        </FormGroup>  
      </Col>   
                    <Col>  
                        <FormGroup row>  
                            <Col sm={5}>  
                            </Col>  
                            <Col sm={1}>  
                          <Button type="submit" color="success">Submit</Button>{' '}  
                            </Col>  
                            <Col sm={1}>  
                                <Button color="danger">Cancel</Button>{' '}  
                            </Col>  
                            <Col sm={5}>  
                            </Col>  
                        </FormGroup>  
                    </Col>  
                </Form>  
            </Container>  
        );  
    }  
  
}  
  
export default Edit; 
