import './Style.css';
import { Table } from 'react-bootstrap';
import {Button}from 'react-bootstrap';
import React, { Component } from 'react'  
import axios from 'axios';  

class AddBook extends Component {
    constructor(props){
        super(props);
    state={
        error:null,
        books:[],
        response:{}
    }
}
    componentDidMount() {
        axios.get('http://localhost:8080/books').then((response) =>{
            this.setState({
                books: response.data
            })
        },
        (error)=>{  
            this.setState({error});
        }
        );
    }
    render(){
        return(
        <div className="bs-example">
            <Table className="table table-striped table-dark">
                <thead>
                    <tr>
                        <th >#</th>
                        <th >BookName</th> 
                        <th>Edition</th> 
                        <th >Author</th>
                        <th >Date</th>
                        <th >Amount</th>
                    </tr>
                </thead>
                <tbody>
                {this.state.books.map((book) => {
            return <tr key={book.id}>
                <td >{book.id}</td>
                <td >{book.bookname}</td>
                <td >{book.edition}</td>
                <td>{book.author}</td>
                <td >{book.date}</td>
                <td >{book.amount}</td>
                <td>
                    <Button color="success" size="sm" className="mr-2">Edit</Button>
                    <Button color="danger" size="sm">Delete</Button></td>
            </tr>
        })}
                </tbody>
            </Table>
            </div>
        );
    }
}
export default AddBook;













































































