import './Style.css';
import { Table } from 'react-bootstrap';
import {Button}from 'react-bootstrap';
import React, { Component } from 'react'  
import axios from 'axios';  

class ListBook extends Component {
    constructor(props){
        super(props);
    this.state={
        error:null,
        books:[],
        response:{}
    }
}
    componentDidMount() {
        axios.get('http://localhost:8080/books').then(response => response.data).then(  
            (result)=>{  
                this.setState({  
                    books:result  
                });  
        },
        (error)=>{  
            this.setState({error});
        }
        )
    }
    // delete(Bookid) {  
    //     const { books } = this.state;     
    //    axios.delete('http://localhost:8080/books/${this.state.bookid}' + Bookid ).then(result=>{  
    //      alert(result.data);  
    //       this.setState({  
    //         response:result,  
    //         books:books.filter(book=>book.bookid !== Bookid)  
    //       });  
    //     });  
    //   }
    delete =()=>{
        const bookid = this.state.bookid;
        const url='http://localhost:8080/books/';
        axios.delete(url + bookid)
            .then(res => {
                console.log(res.data);
            })
            .catch((err) => {
                console.log(err);
            })
    }

    render(){
        const{error,books}=this.state;  
        if(error){  
            return(  
                <div>Error:{error.message}</div>  
            )  
        }  
        else  
        {
        return(
        <div>
            <Table>
                <thead className="btn-primary">
                    <tr>
                        <th >BookId</th>
                        <th >BookName</th> 
                        <th>Edition</th> 
                        <th >Author</th>
                        <th >Date</th>
                        <th >Amount</th>
                    </tr>
                </thead>
                <tbody>
                {this.state.books.map((book)=> {
                return (<tr key={book.bookid}>
                <td >{book.bookid}</td>
                <td >{book.bookname}</td>
                <td >{book.edition}</td>
                <td>{book.author}</td>
                <td >{book.date}</td>
                <td >{book.amount}</td>
                <td><Button size="la" classname="mr-2" variant="info" 
                onClick={() => this.props.edit(book.bookid)}>Edit</Button>       
                    <Button variant="danger" 
                onClick={() => this.delete(book.bookid)}>Delete</Button>
                </td>
            </tr>
        )
                })}
                </tbody>
            </Table>
            </div>
        );
    }
}
}
export default ListBook;


